import React from "react";
import { useParams, Navigate } from "react-router-dom";
import { useRef, useState, useEffect } from "react";
import Home from "./Home";
import { Image } from "react-bootstrap";
import NavigBar from "./NavigBar";

const MovieDetail = () => {
  const { id } = useParams();
  const hasFetched = useRef(false);
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (!hasFetched.current) {
      fetch(`http://omdbapi.com/?apikey=470179fa&i=${id}`)
        .then((response) => response.json())
        .then((data) => {
          setMovies(data);
          setIsLoading(true);
        });
      hasFetched.current = true;
    }
  }, [id]);

  return (
    <>
      <NavigBar />
      <div className="col-md-5 mx-auto mt-5 mb-5">
        <div className="d-flex justify-content-left">
          {isLoading ? (
            movies.length !== 0 ? (
              <>
                <div className="d-flex justify-content-left pr-5">
                  <Image
                    src={movies.Poster}
                    width={250}
                    height={375}
                    className="img-no-hover"
                  />
                </div>
                <div classname="text-left justify-content-left mx-5 pl-5">
                  <p>{movies.Plot}</p>
                </div>
              </>
            ) : (
              <Navigate to="/" />
            )
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </>
  );
};

export default MovieDetail;
