import { React, useState, useRef, useEffect } from "react";
import { useParams, Link, Navigate } from "react-router-dom";
import MoviePoster from "./MoviePoster";
import "./index.css";
import Home from "./Home";
import { Image, Container, Row, Alert } from "react-bootstrap";

const MovieCards = () => {
  const { q } = useParams();
  const hasFetched = useRef(false);
  const response = useRef(false);
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    hasFetched.current = false;
    if (!hasFetched.current) {
      fetch(`http://omdbapi.com/?apikey=470179fa&s=${q}`)
        .then((response) => response.json())
        .then((data) => {
          if (data.Response === "True") {
            console.log("passou");
            setMovies(data.Search);
            response.current = true;
            setIsLoading(true);
          } else {
            console.log("oupa");
            response.current = false;
            setIsLoading(true);
          }
        });
      hasFetched.current = true;
      setIsLoading(false);
    }
  }, [q]);

  return (
    <>
      <Home />
      <div className="mt-5 px-3 py-3 justify-content-left d-flex text-center flex-wrap align-items-center">
        {isLoading ? (
          response.current ? (
            movies.map((movie) => {
              return (
                <MoviePoster
                  name={movie.Title}
                  key={movie.imdbID}
                  id={movie.imdbID}
                  poster={movie.Poster}
                />
              );
            })
          ) : (
            <div className="justify-content-center align-items-center text-center">
              <h2 className="text-center">Movie Not Found!</h2>
            </div>
          )
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </>
  );
};

export default MovieCards;
