import React from "react";
import "./index.css";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const MoviePoster = ({ name, id, poster }) => {
  return (
    <div className="grid-container">
      <Link to={`/titles/${id}`}>
        <img src={poster} alt="" />
      </Link>
    </div>
  );
};

export default MoviePoster;
