import React from "react";
import "./index.css";
import { Link } from "react-router-dom";

const MoviePoster = ({ name, id, poster }) => {
  return (
    <div className="grid-container">
      <div className="grid-item">
        <Link to={`/titles/${id}`}>
          <img src={poster} alt="" />
        </Link>
      </div>
    </div>
  );
};

export default MoviePoster;
