import React, { Component, useContext } from "react";
import "./index.css";
import { Link } from "react-router-dom";
import axios from "axios";
import "./index.css";
import { UserContext } from "./components/UserContext";

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movie: "",
      review: "",
    };
  }

  componentDidMount() {
    document.title = "Search";
    axios.get("http://localhost:5000/api/4").then((response) => {
      this.setState({ review: response.data[0].review });
    });
  }

  render() {
    const { user, setUser } = useContext(UserContext);

    return (
      <span id="header" className="d-flex">
        <div className="mt-5 col-md-4 container justify-content-center">
          <form action="submit" className="form-inline">
            <div className="form-group mx-auto mb-2">
              <input
                onChange={(e) => this.setState({ movie: e.target.value })}
                type="search"
                placeholder="Insert movie"
              />
            </div>

            <Link to={this.state.movie !== "" ? `/${this.state.movie}` : {}}>
              <button className="btn btn-primary mb-2" type="submit">
                Submit
              </button>
            </Link>
          </form>
        </div>
        <p></p>
      </span>
    );
  }
}

export default Home;
