import React, { Component } from "react";
import "./index.css";
import { Link } from "react-router-dom";
import { Image, Container } from "react-bootstrap";
import imgNotFound from "./images/notFound.jpg";

const MoviePoster = ({ name, id, poster }) => {
  console.log(poster === "N/A");

  return (
    <div className="container-item-sm">
      <Link to={`/titles/${id}`}>
        {poster !== "N/A" ? (
          <Image src={poster} width={150} height={200} className="img"></Image>
        ) : (
          <Image
            src={imgNotFound}
            width={150}
            height={200}
            className="img"
          ></Image>
        )}
      </Link>
      <p className="flex-wrap">{name}</p>
    </div>
  );
};

export default MoviePoster;
