import React, { useEffect, useContext } from "react";
import NavigBar from "./NavigBar";
import { UserContext } from "./UserContext";
import { Link } from "react-router-dom";
const Welcome = () => {
  const { user, setUser } = useContext(UserContext);
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  useEffect(() => (document.title = "Welcome"));
  return (
    <div className="container col-lg-2">
      <div className="mt-5 col-lg-100 justify-content-center align-items-center">
        <h1>Welcome!</h1>
        <form
          action="/add"
          method="post"
          className="form-inline mt-4"
          onSubmit={(e) => handleSubmit(e)}
        >
          <div className="form-group">
            <input
              type="text"
              placeholder="First Name"
              className="form-control mb-3"
              onChange={(e) => {
                setUser({ firstName: e.target.value, lastName: user.lastName });
              }}
            />
            <input
              type="text"
              placeholder="Last Name"
              className="form-control"
              onChange={(e) => {
                setUser({
                  firstName: user.firstName,
                  lastName: e.target.value,
                });
              }}
            />
          </div>
          <Link to="/">
            <button className="btn btn-primary mt-3">Submit</button>
          </Link>
        </form>
      </div>
    </div>
  );
};

export default Welcome;
