import "./index.css";
import { Link } from "react-router-dom";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Necas from "./Necas";
import { useState, useEffect } from "react";
import Button from "./Button.js";
import Form from "./Form.js";

const App = () => {
  const [title, setTitle] = useState("");
  const onClick = () => {
    console.log("Click");
  };

  useEffect(() => {
    const getMovie = async () => {
      const movieFromServer = await fetchMovie();
      setTitle(movieFromServer.Title);
    };
    getMovie();
  }, []);

  const onAdd = (t) => {
    fetchMovie(t);
  };

  const fetchMovie = async (key) => {
    const res = await fetch(
      "https://www.omdbapi.com/?apikey=470179fa&t=" + key.replace(/+/g, " ")
    );
    const data = await res.json();
    return data;
  };
  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div className="container">
              <Form onAdd={onAdd} />

              <Button onClick={onClick} />
            </div>
          }
        />
        <Route path="/necas" element={<Necas text={title} />} />
      </Routes>
    </Router>
  );
};

export default App;
