import React, { Component } from "react";
import "./index.css";
import { Link } from "react-router-dom";

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movie: "",
    };
  }
  render() {
    return (
      <div className="align-self-center justify-content-center">
        <form action="submit">
          <input
            onChange={(e) => this.setState({ movie: e.target.value })}
            type="search"
            placeholder="Insert movie"
          />
          <Link to={`/${this.state.movie}`}>
            <button className="btn btn-sm btn-success mt-2">Submit</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default Home;
