import React from "react";
import { useParams, Navigate } from "react-router-dom";
import { useRef, useState, useEffect } from "react";
import Home from "./Home";
import { Image } from "react-bootstrap";
import NavigBar from "./NavigBar";

const MovieDetail = () => {
  const { id } = useParams();
  const hasFetched = useRef(false);
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const listedKeys = [
    "Title",
    "Year",
    "Rated",
    "Released",
    "Runtime",
    "Genre",
    "Director",
    "Writer",
    "Actors",
    "Awards",
    "Box Office",
    "Production",
  ];
  useEffect(() => {
    if (!hasFetched.current) {
      fetch(`http://omdbapi.com/?apikey=470179fa&i=${id}`)
        .then((response) => response.json())
        .then((data) => {
          setMovies(data);
          setIsLoading(true);
        });
      hasFetched.current = true;
    }
  }, [id]);

  return (
    <>
      <NavigBar />
      <div className="col-md-10 mx-auto mt-5 mb-5">
        <div className="d-flex justify-content-left">
          {isLoading ? (
            movies.length !== 0 ? (
              <>
                <div className="d-flex justify-content-left px-5">
                  <Image
                    src={movies.Poster}
                    width={300}
                    height={450}
                    className="img-no-hover"
                  />
                </div>
                <div className="inline-flex justify-content-left">
                  <div className="d-flex col-md-10 pl-5 ml-5">
                    <h1>{movies.Title}</h1>
                  </div>
                  <div className="d-flex col-md-10 pl-5 ml-5">
                    <p>{movies.Plot}</p>
                  </div>
                  <div className="d-flex col-md-10 pl-5 ml-5 justify-content-left">
                    <ul className="list-group justify-content-left">
                      {Object.keys(movies)
                        .filter((value) => {
                          return listedKeys.includes(value);
                        })
                        .map((key, index) => {
                          return (
                            <li
                              className="list-group-item border-0 p-0"
                              key={key}
                            >
                              <p>
                                <b>{`${key}:`}</b>
                                {` ${movies[key]}`}
                              </p>
                            </li>
                          );
                        })}
                    </ul>
                  </div>
                </div>
              </>
            ) : (
              <Navigate to="/" />
            )
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </>
  );
};

export default MovieDetail;
