import React, { Component } from "react";
import "./index.css";
import { Link } from "react-router-dom";
import NavigBar from "./NavigBar";

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movie: "",
    };
  }
  render() {
    return (
      <div className="h-100">
        <NavigBar />
        <div className="col-lg-4 col-md-4 col-sm-4 container justify-content-center">
          <form action="submit" className="form-inline">
            {/* <div class="form-group mx-sm-3 mb-2">
    <label for="inputPassword2" class="sr-only">Password</label>
    <input type="password" class="form-control" id="inputPassword2" placeholder="Password">
  </div> */}
            <div className="form-group mx-sm-3 mb-2">
              <input
                onChange={(e) => this.setState({ movie: e.target.value })}
                type="search"
                placeholder="Insert movie"
              />
            </div>

            <Link to={`/${this.state.movie}`}>
              <button className="btn btn-primary mb-2" type="submit">
                Submit
              </button>
            </Link>
          </form>
        </div>
      </div>
    );
  }
}

export default Home;
