import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Review = () => {
  const [user, setUser] = useState("");
  const [review, setReview] = useState("");
  const [availableReviews, setAvailableReviews] = useState([]);
  const { id } = useParams();

  const handleSubmit = () => {
    axios
      .post(`/api/${id}`, {
        user: user,
        review: review,
        score: 1,
      })
      .then(console.log("Successful insert"));
  };

  useEffect(() => {
    axios.get(`/api/${id}`).then((response) => {
      setAvailableReviews(response.data);
    });
  }, []);

  return (
    <div className="container">
      <div className="d-flex">
        <div className="col-md-4 mx-5">
          <form
            className="form-inline"
            onSubmit={() => {
              handleSubmit();
            }}
          >
            <div className="form-group col-lg-100 mx-auto mb-5">
              <input
                type="text"
                placeholder="User"
                className="mb-4 form-control"
                onChange={(e) => {
                  setUser(e.target.value);
                }}
              />

              <textarea
                placeholder="Insert review"
                className="h-100 form-control rounded-0"
                onChange={(e) => {
                  setReview(e.target.value);
                }}
                rows="5"
              />
            </div>
            <button className="btn btn-primary mb-2" type="submit">
              Submit
            </button>
          </form>
        </div>
        <div className="col-md-4 mx-5">
          {availableReviews.map((data) => {
            return (
              <div className="my-5 border border-primary" key={`${data.id}`}>
                {data.user}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Review;
